import React, {useState, useEffect} from 'react';
import axios from "axios";
import { withFormik, Form, Field } from "formik";

function addVacation({status}) {
    const [vacation, setVacation] = useState()

    useEffect(() => {
        status && setVacation(vacation => [vacation, status])
      },[status])

    return (
        <div>
            <Form>
                <Field type="text" name="place" placeholder="NewVacation"/><br/>
                <button type="submit">Submit!</button>
            </Form>
            <div className="outCont">
                {vacation.map(trip => (
                <div className="output">
                    <ul key={trip.id}>
                        <li>Add Vacation:  {trip.place}</li>
                    </ul>
                </div>
                ))}
            </div>
        </div>
    )
}

const FormikAddVacaction = withFormik ({
    mapPropsToValues({place }){
        return{
            place: place || "",
        };
    },

    handleSubmit(values, {setStatus, resetForm}) { 
        axios.post('https://reqres.in/api/vacations', values) 
              .then(response => { 
                  console.log(response)
                  setStatus(response.data); 
                  resetForm()
                }) 
              .catch(err => console.log(err.response));
        }

})(addVacation);

export default FormikAddVacaction;
