import React from 'react'
import axios from "axios";
import { withFormik, Form, Field } from "formik";

function addVacation({values, status}) {
    const [user, setUser] = useState([])
    useEffect(() => {
        status && setUser(user => [...user, status])
      },[status])
    return (
        <Container>
            <h1>Fast & the Formikrs</h1>
            <Form>
                <Field type="text" name="name" placeholder="New Vacation"/><br/>
                <button type="submit">Submit!</button>
            </Form>
            <div className="outCont">
                {user.map(individ => (
                <div className="output">
                    <ul key={individ.id}>
                        <li>Name:  {individ.name}</li>
                    </ul>
                </div>
                ))}
            </div>
        </Container>
    )
}

const FormikAddVacaction = withFormik ({
    mapPropsToValues({name, email, password, quote, termsOfService }){
        return{
            name: name || "",
        };
    },
    // validationSchema: Yup.object().shape({
    //     name: Yup.string().required("Please Enter Name"),
    //     password: Yup.string().required("No pass Go! Please Enter your password!"),
    //     termsOfService: Yup.boolean().oneOf([true], 'Must Agree to Terms!'),
    // }),
    handleSubmit(values, {setStatus, resetForm}) { 
        axios.post('https://reqres.in/api/users', values) 
              .then(response => { 
                  console.log(response)
                  setStatus(response.data); 
                  resetForm()
                }) 
              .catch(err => console.log(err.response));
        }

})(addVacation);
export default FormikAddVacaction;
