import React, {useState, useEffect} from 'react';
import axios from "axios";
import { withFormik, Form, Field } from "formik";

function addVacation({status}) {
    const [vacation, setVacation] = useState([])

    useEffect(() => {
        status && setVacation(vacation => [...vacation, status])
      },[status])

    return (
        <div>
            <h1>Fast & the Formikrs</h1>
            <Form>
                <Field type="text" name="addVacation" placeholder="New Vacation"/><br/>
                <button type="submit">Submit!</button>
            </Form>
            <div className="outCont">
                {vacation.map(trip => (
                <div className="output">
                    <ul key={trip.id}>
                        <li>Add Vacation:  {trip.addVacation}</li>
                    </ul>
                </div>
                ))}
            </div>
        </div>
    )
}

const FormikAddVacaction = withFormik ({
    mapPropsToValues({addVacation }){
        return{
            addVacation: addVacation || "",
        };
    },

    handleSubmit(values, {setStatus, resetForm}) { 
        axios.post('https://reqres.in/api/', values) 
              .then(response => { 
                  console.log(response)
                  setStatus(response.data); 
                  resetForm()
                }) 
              .catch(err => console.log(err.response));
        }

})(addVacation);

export default FormikAddVacaction;
